package com.clase.info;

public class Ejercicio3examen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
        int b = 7;
        int c = 9;

        // Muestra la suma de las tres variables con System.out.println
        System.out.println(a + b + c);

        // Cambia el valor de la variable c
        c = 12;

        // Muestra la suma de las tres variables con System.out.printf
        System.out.printf("La suma de %d, %d y %d es %d\n", a, b, c, a + b + c);
    }

	}


